import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [budgets, setBudgets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      const [statsRes, transactionsRes, budgetsRes] = await Promise.all([
        fetch('/api/transactions/stats/summary', { headers }),
        fetch('/api/transactions?page=1&limit=5', { headers }),
        fetch('/api/budgets', { headers })
      ]);

      const statsData = await statsRes.json();
      const transactionsData = await transactionsRes.json();
      const budgetsData = await budgetsRes.json();

      setStats(statsData);
      setRecentTransactions(transactionsData.transactions || []);
      setBudgets(budgetsData.budgets || []);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  // Prepare chart data
  const categoryData = {};
  recentTransactions.forEach(transaction => {
    if (transaction.type === 'expense') {
      categoryData[transaction.category] = (categoryData[transaction.category] || 0) + transaction.amount;
    }
  });

  const barData = {
    labels: Object.keys(categoryData),
    datasets: [{
      label: 'Expenses by Category',
      data: Object.values(categoryData),
      backgroundColor: 'rgba(54, 162, 235, 0.6)',
      borderColor: 'rgba(54, 162, 235, 1)',
      borderWidth: 1,
    }],
  };

  const doughnutData = {
    labels: Object.keys(categoryData),
    datasets: [{
      data: Object.values(categoryData),
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
        '#4BC0C0',
        '#9966FF',
        '#FF9F40',
      ],
    }],
  };

  return (
    <div>
      <h1>Dashboard</h1>

      {/* Summary Cards */}
      <div className="dashboard-summary">
        <div className="summary-card">
          <h3>Total Income</h3>
          <div className="amount positive">${stats?.totalIncome?.toFixed(2) || '0.00'}</div>
        </div>
        <div className="summary-card">
          <h3>Total Expenses</h3>
          <div className="amount negative">${stats?.totalExpenses?.toFixed(2) || '0.00'}</div>
        </div>
        <div className="summary-card">
          <h3>Net Income</h3>
          <div className={`amount ${stats?.netIncome >= 0 ? 'positive' : 'negative'}`}>
            ${stats?.netIncome?.toFixed(2) || '0.00'}
          </div>
        </div>
        <div className="summary-card">
          <h3>Transactions</h3>
          <div className="amount">{stats?.transactionCount || 0}</div>
        </div>
      </div>

      {/* Charts */}
      <div className="dashboard-grid">
        <div className="card">
          <h2>Expenses by Category</h2>
          <div className="chart-container">
            <Bar data={barData} options={{ responsive: true, maintainAspectRatio: false }} />
          </div>
        </div>
        <div className="card">
          <h2>Expense Distribution</h2>
          <div className="chart-container">
            <Doughnut data={doughnutData} options={{ responsive: true, maintainAspectRatio: false }} />
          </div>
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2>Recent Transactions</h2>
          <Link to="/transactions/add" className="btn btn-primary">Add Transaction</Link>
        </div>
        {recentTransactions.length > 0 ? (
          <div className="transaction-list">
            {recentTransactions.map(transaction => (
              <div key={transaction._id} className="transaction-item">
                <div className="transaction-info">
                  <h4>{transaction.description}</h4>
                  <p>{transaction.category} â€¢ {new Date(transaction.date).toLocaleDateString()}</p>
                </div>
                <div className={`transaction-amount ${transaction.type}-amount`}>
                  {transaction.type === 'income' ? '+' : '-'}${transaction.amount.toFixed(2)}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No transactions yet. <Link to="/transactions/add">Add your first transaction</Link></p>
        )}
      </div>

      {/* Budget Overview */}
      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2>Budget Overview</h2>
          <Link to="/budgets/add" className="btn btn-primary">Add Budget</Link>
        </div>
        {budgets.length > 0 ? (
          <div className="budget-list">
            {budgets.slice(0, 3).map(budget => {
              const percentage = budget.amount > 0 ? (budget.spent / budget.amount) * 100 : 0;
              return (
                <div key={budget._id} className="budget-item">
                  <div className="budget-header">
                    <h3>{budget.name}</h3>
                    <span>{budget.category}</span>
                  </div>
                  <div className="budget-progress">
                    <div className="progress-bar">
                      <div
                        className={`progress-fill ${percentage > 90 ? 'danger' : percentage > 75 ? 'warning' : ''}`}
                        style={{ width: `${Math.min(percentage, 100)}%` }}
                      ></div>
                    </div>
                  </div>
                  <div className="budget-stats">
                    <span>${budget.spent?.toFixed(2) || '0.00'} spent</span>
                    <span>${budget.amount.toFixed(2)} budget</span>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <p>No budgets set. <Link to="/budgets/add">Create your first budget</Link></p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
